mapper.insert:
INSERT INTO User (
    user_id, username, password, weight, height, age, gender, goal_calories, goal_frequency, created_at, last_login
) VALUES (
    #{userId}, #{username}, #{password}, #{weight}, #{height}, #{age}, #{gender}, #{goalCalories}, #{goalFrequency}, #{createdAt}, #{lastLogin}
)

mapper.update:
UPDATE User
SET last_login = #{lastLogin}
WHERE user_id = #{userId}
